    static void minimumBribes(int[] q) {
        boolean flag = true;
        int result = 0;

        for (int i = 0; i < q.length; i++) {
            if (q[i] > i + 3) {
                System.out.println("Too chaotic");
                flag = false;
                break;
            }
        }

        if (flag) {
            int i, j, temp;
            boolean swapped;
            int n = q.length;
            for (i = 0; i < n - 1; i++)
            {
                swapped = false;
                for (j = 0; j < n - i - 1; j++)
                {
                    if (q[j] > q[j + 1])
                    {
                        temp = q[j];
                        q[j] = q[j + 1];
                        q[j + 1] = temp;
                        swapped = true;
                        result++;
                    }
                }
                if (swapped == false)
                    break;
            }
            System.out.println(result);
        }
    }
